import React from 'react';
import { useQuery } from '@tanstack/react-query';
import { useParams } from 'react-router-dom';
import { Skeleton, Typography, Image } from 'antd';
import { getPerson } from '../../queries/queries';
import { FacebookIcon } from '../../components/Icon/FacebookIcon/FacebookIcon';
import { TwitterIcon } from '../../components/Icon/TwitterIcon/TwitterIcon';
import { InstagramIcon } from '../../components/Icon/InstagramIcon/InstagramIcon';

export const PersonPage = (): JSX.Element => {
  const pageParams = useParams();
  const personId = pageParams?.id;

  const { isLoading, isError, data, error } = useQuery({
    queryKey: ['person', personId],
    queryFn: () => getPerson(personId),
    enabled: !!personId,
  });

  if (isLoading) {
    return <Skeleton />;
  }

  if (isError) {
    return <span>Error: {error.message}</span>;
  }

  return (
    <>
      <div className='main-info'>
        <Image
          width={200}
          height={200}
          src={`data:${data?.data?.personPic?.contentType};base64,${data?.data?.personPic?.data}`}
          fallback='data:image/png;base64,'
        />
        <Typography.Title>{data?.data?.personName}</Typography.Title>
        <Typography.Title level={4}>{data?.data?.personFullName}</Typography.Title>
        {data?.data?.personWebsite ? (
          <Typography.Link title={`${data?.data?.personName} website`} strong>
            {data?.data?.personWebsite}
          </Typography.Link>
        ) : null}
      </div>
      <div className='socials'>
        {data?.data?.personSocials?.facebook ? (
          <Typography.Link
            title={`${data?.data?.personName} facebook page`}
            href={data?.data?.personSocials?.facebook}
          >
            <FacebookIcon />
          </Typography.Link>
        ) : null}
        {data?.data?.personSocials?.twitter ? (
          <Typography.Link
            title={`${data?.data?.personName} twitter page`}
            href={data?.data?.personSocials?.twitter}
          >
            <TwitterIcon />
          </Typography.Link>
        ) : null}
        {data?.data?.personSocials?.instagram ? (
          <Typography.Link
            title={`${data?.data?.personName} instagram page`}
            href={data?.data?.personSocials?.instagram}
          >
            <InstagramIcon />
          </Typography.Link>
        ) : null}
      </div>
      <div className='personal-info'>
        <Typography.Title level={5}>Personal info</Typography.Title>
        <Typography.Paragraph>
          <Typography.Text strong>Person mostly knwon as:</Typography.Text>
          <Typography.Text>
            {data?.data?.personPositions?.map?.(
              (p: { _id: string; positionName: string }) => p.positionName,
            )}
          </Typography.Text>
        </Typography.Paragraph>

        <Typography.Paragraph>
          <Typography.Text strong>Date of birth:</Typography.Text>
          <Typography.Text>{data?.data?.personDoB}</Typography.Text>
        </Typography.Paragraph>

        <Typography.Paragraph>
          <Typography.Text strong>Place of birth:</Typography.Text>
          <Typography.Text>{data?.data?.personPlaceOfBirth?.countryName}</Typography.Text>
        </Typography.Paragraph>

        <Typography.Paragraph>
          <Typography.Text strong>Gender:</Typography.Text>
          <Typography.Text>{data?.data?.personGender?.genderName}</Typography.Text>
        </Typography.Paragraph>

        <Typography.Paragraph>
          <Typography.Text strong>About:</Typography.Text>
          <Typography.Text>{data?.data?.personDescription}</Typography.Text>
        </Typography.Paragraph>
      </div>
      <div className='gallery'>
        {data.data?.personGalleryPhotos?.map?.(
          (img: { _id: string; contentType: string; data: string }) => (
            <Image
              key={img._id}
              height={200}
              src={`data:${img.contentType};base64,${img.data}`}
              fallback='data:image/png;base64,'
            />
          ),
        )}
      </div>
    </>
  );
};
